import{_ as i,o as t,c as a,ae as l}from"./chunks/framework.CLid5Cgc.js";const x=JSON.parse('{"title":"薛兆丰的经济学","description":"","frontmatter":{"title":"薛兆丰的经济学"},"headers":[],"relativePath":"notes/xue-zhao-feng-jing-ji-xue/index.md","filePath":"notes/xue-zhao-feng-jing-ji-xue/index.md"}'),n={name:"notes/xue-zhao-feng-jing-ji-xue/index.md"};function o(r,e,s,_,d,c){return t(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="薛兆丰的经济学" tabindex="-1">薛兆丰的经济学 <a class="header-anchor" href="#薛兆丰的经济学" aria-label="Permalink to &quot;薛兆丰的经济学&quot;">​</a></h1><ul><li>边际效应：当持续加大某一东西的投入，新增的每一单位投入所得产出越少。</li><li>需求第一定律：价格与需求成反比，当价格越高，供给者会越多，从而供过于求。</li><li>需求第二定律：买卖双方谁越想促成交易，谁的成本就高。</li><li>需求第三定律：当商品附加费（运费、关税）提高时，各品质商品价格差异降低，好的商品销量更好。</li><li>计划经济的三个障碍：人们的对事物认定的价值不可衡量、普查数据受人们的预期（奖惩）发生变化所以不易获取、实施计划经济的奖惩制度不完善。</li><li>价格管制：会让人囤积货物，不愿意生产，抑制社会发展。</li><li>权利：是他人给予，大众认为正确的选择。</li><li>自由：并非免费，因为要动用他人的经济权利和经济资源（场地）。</li><li>冷暴力：长期被动地接受不喜欢的事物导致的。</li></ul>',2)])])}const f=i(n,[["render",o]]);export{x as __pageData,f as default};
